import React, { useCallback, useEffect, useState } from 'react';
import dayjs from 'dayjs';
import styled from 'styled-components';
import { upComings } from '../../API/upComings';
import { nowPlayings } from '../../API/nowPlayings';
import { movies } from '../../API/getMovieKeys';
import MyModal from './MovieModal';

const Wrapper = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-flow: row wrap;
`;

const Img = styled.img`
  width: 200px;
  height: 260px;
`;

const Title = styled.p`
  width: 240px;
  color: white;
  word-wrap: normal;
  text-align: center;
  padding-right: 50px;
  margin-top: 10px;
`;

const Div = styled.div`
  width: 218px;
  height: 300px;
  margin: 8px 8px 20px 8px;
  display: flex;
  flex-direction: column;
  color: white;
  text-align: center;
`;

const Poster = () => {
  const [upcoming, setUpcoming] = useState({});
  const [nowPlay, setNowPlay] = useState({});
  const [movieId, setMovieId] = useState({});
  const [modal, setModal] = useState(false);
  const [image, setImage] = useState("");
  const [title, setTitle] = useState("");
  const later = dayjs().add(14, 'd').format('YYYY-MM-DD');
  const today = dayjs().format('YYYY-MM-DD');
  const ago = dayjs().subtract(14, 'd').format('YYYY-MM-DD');

  const getUpComings = useCallback(() => {
    upComings().then((res) => {
      setUpcoming(res);
    });
  }, []);

  const getNowPlayings = useCallback(() => {
    nowPlayings(1).then((res1) => {
      setNowPlay((prev) => {
        return { ...prev, res1 };
      });
    });

    nowPlayings(2).then((res2) => {
      setNowPlay((prev) => {
        return { ...prev, res2 };
      });
    });

    nowPlayings(3).then((res3) => {
      setNowPlay((prev) => {
        return { ...prev, res3 };
      });
    });
  }, []);

  const close = () => {
    setModal(false);
  }

  // const getMovies = useCallback(() => {
  //   movies().then((res) => {
  //     setMoviesId(res);
  //   });
  // }, []);

  useEffect(() => {
    getUpComings();
    getNowPlayings();
    // getMovies();
  }, []);

  return (
    <Wrapper>
      {upcoming.results.map((e) => {
        if (e.release_date <= later) {
          return (
            <Div onClick={() => {
              setMovieId(e.id);
              setImage(e.poster_path);
              setTitle(e.title);
              setModal(true);
            }}>
              <Img src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}></Img>
              <Title>
                개봉예정일 : {e.release_date}
                <br></br>제목 : {e.title}
              </Title>
            </Div>
          );
        }
      })}
      {nowPlay.res1.results.map((e) => {
        if (ago <= e.release_date && e.release_date <= today) {
          return (
            <Div onClick={() => {
              setMovieId(e.id);
              setImage(e.poster_path);
              setTitle(e.title);
              setModal(true);
            }}>
              <Img src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}></Img>
              <Title>
                개봉일 : {e.release_date}
                <br></br>제목 : {e.title}
              </Title>
            </Div>
          );
        }
      })}
      {nowPlay.res2.results.map((e) => {
        if (ago <= e.release_date && e.release_date <= today) {
          return (
            <Div onClick={() => {
              setMovieId(e.id);
              console.log("e.id", e.id);
              setImage(e.poster_path);
              console.log(e.poster_path);
              setTitle(e.title);
              setModal(true);
            }}>
              <Img src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}></Img>
              <Title>
                개봉일 : {e.release_date}
                <br></br>제목 : {e.title}
              </Title>
            </Div>
          );
        }
      })}
      {nowPlay.res3.results.map((e) => {
        if (ago <= e.release_date && e.release_date <= today) {
          return (
            <Div onClick={() => {
              setMovieId(e.id);
              setImage(e.poster_path);
              setTitle(e.title);
              setModal(true);
            }}>
              <Img src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}></Img>
              <Title>
                개봉일 : {e.release_date}
                <br></br>제목 : {e.title}
              </Title>
            </Div>
          );
        }
      })}
      <MyModal open={modal} title={title} image={image} videoId={movieId} close={close} />
    </Wrapper>
  );
};

export default Poster;
