import React, { useEffect } from "react";
import Modal from "react-modal";
import MoviesInfo from "../movie/MoviesInfo";
import PlayMovie from "../movie/PlayMovie";
import Playlist from "../movie/Playlist";
import styled from "styled-components";
import { createPortal } from "react-dom";

const StyledModal = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.8);
  background-filter: blur(5px);
`;

const Div = styled.div`
  width: 90%;
  height: 1000px;
  color: white;
  overflow-y: scroll;
`;

const Ex = styled.div`
  width: 100%;
  height: 1000px;
`;

const ClickMovie = () => {
  useEffect(() => {
    document.body.style =
      "background-color: black; margin:0px; padding: 0px; box-sizing: border-box; overflow:hidden";
    return () => (document.body.style = `background-color: black; margin:0px; padding: 0px; box-sizing: border-box;overflow:auto`);
  }, []);
  return createPortal(
    <StyledModal width="90%">
      <Div>
        <PlayMovie videoId="qT_fjEgPsaA" />
        <Div>
          <ex>줄거리</ex>
          <div>영화 정보</div>
        </Div>
        <Div>Playlist</Div>
      </Div>
    </StyledModal>,
    document.getElementById("modal")
  );
};

export default ClickMovie;
