  top: 0px;
  right: 0px;
  left: 0px;
  bottom: 0px;
  background-color: white(0, 0, 0, 0.6);
`;

const Div = styled.div`
  color: white;
  display: flex;
`;

const ClickMovie = () => {
    console.log("modalopen")
  return createPortal(
    <StyledModal>
