import React from 'react';
import styled from 'styled-components';
import { useState } from 'react';
import { apiKey, PLAYLISTS, VIDEOS, YOUTUBEURL } from '../../API/apis';
import YouTube from 'react-youtube'
import ids from '../../channelId/ids';
import { useEffect } from 'react';

const Div=styled.div`
    width: 100%;
    height: 500px;
    background: white;
    border: 1px solid red;
`

const Header = () => {
    const [loading, setLoading] = useState(true);
    const [playlists, setPlaylists]  = useState([]);
    const [videos, setViedos] = useState([]);

    const getVideos = async () => {
        const plist = await (
            await fetch (
                `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MavelKorea}&maxResult=50&part=snippet,contentDetails`
            )
        ).json().catch(e => console.log(e))
        setPlaylists(plist);
        setLoading(false);
        console.log(plist.items);
    }

    useEffect(() => {
        getVideos();
    },[])


    return (
        <Div>
            <YouTube
            videoId='plist.items.id'></YouTube>
        </Div>
    );
};

export default Header;
