import React, { useCallback, useEffect, useState } from 'react';
import dayjs from 'dayjs';
import styled from 'styled-components';
import { upComings } from '../../API/upComings';
import { nowPlayings } from '../../API/nowPlayings';
import { movies } from '../../API/getMovieId';

const Wrapper = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-flow: row wrap;
`;

const Img = styled.img`
  width: 200px;
  height: 260px;
`;

const Title = styled.p`
  width: 240px;
  color: white;
  word-wrap: normal;
  text-align: center;
  padding-right: 50px;
  margin-top: 15px;
`;

const Div = styled.div`
  width: 220px;
  height: 300px;
  margin: 8px;
  display: flex;
  flex-direction: column;
  color: white;
  text-align: center;
`;

const Poster = () => {
  const apiKey = 'b69ce63df91b692b402e8436d1099819';
  const TMDBURL = 'https://api.themoviedb.org/3/movie';
  const [upcoming, setUpcoming] = useState({});
  const [nowPlay, setNowPlay] = useState({});
  const [nPlayPage, setNPlayPage] = useState(0);
  const [moviesId, setMoviesId] = useState({});
  const [latest, setLatest] = useState({});
  const later = dayjs().add(14, 'd').format('YYYY-MM-DD');
  const today = dayjs().format('YYYY-MM-DD');
  const ago = dayjs().subtract(14, 'd').format('YYYY-MM-DD');

  const getUpcomings = useCallback(() => {
    upComings().then((res) => {
      setUpcoming(res);
    });
  }, []);

  const getNowPlayings = useCallback(() => {
    nowPlayings(1).then((res1) => {
      setNowPlay((prev) => {
        return {...prev, res1}
      });
    });
    nowPlayings(2).then((res2) => {
      setNowPlay((prev) => {
        return {...prev, res2}
      });
    });
    nowPlayings(3).then((res3) => {
      setNowPlay((prev) => {
        return {...prev, res3}
      });
    });
  }, []);

  const getMovies = useCallback(() => {
    movies().then((res) => {
      setMoviesId(res);
    });
  }, []);

  useEffect(() => {
    getUpcomings();
    getNowPlayings();
    getMovies();
  }, []);

  console.log(nowPlay.res1.results);
