import React, { useContext } from 'react';
import styled from 'styled-components';
import Modal from 'react-modal';
import MovieIdsContext from '../../contexts/bookMarkIds';

const modalStyle = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backdropFilter: 'blur(5px)',
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    width: '100%',
    zIndex: 10,
    overflow: 'hidden',
    padding: '0px',
    display: 'flex',
    justifyContent: 'center',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'black',
    border: 'none',
    color: 'white',
    borderRadius: '20px',
    overflowy: 'scroll',
    WebkitOverflowScrolling: 'touch',
    zIndex: 10,
    width: '80%',
    marginLeft: '85px',
  },
};

const NoBookMark = styled.div`
  text-align: center;
  color: red;
`;

const BookModal = (props) => {
  const { movieIds, deleteMovieId, setMovieIds } = useContext(MovieIdsContext);

  return (
    <Modal
      isOpen={props.open}
      onRequestClose={() => {
        props.close();
      }}
      ariaHideApp={false}
      style={modalStyle}
    >
      {movieIds.length === 0 && <NoBookMark>즐겨찾기 한 영화가 없습니다.</NoBookMark>}
      {movieIds.length !== 0 && (
        <div>
          <div>
            <div>
              <div></div>
            </div>
          </div>
        </div>
      )}
    </Modal>
  );
};

export default BookModal;
