import React, { useEffect, useState } from "react";
import dayjs from "dayjs";
import styled from "styled-components";

const Img = styled.img`
  width: 200px;
  height: 260px;
`
const Div = styled.div`
  width: 100%;
  height: 100%;
  padding: 20px;
  display: flex;
  flex-flow: wrap;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
`;

const Poster = () => {
  const apiKey = "b69ce63df91b692b402e8436d1099819";
  const TMDBURL = "https://api.themoviedb.org/3/movie";
  const [upcoming, setUpcoming] = useState({});
  const [nowPlay, setNowPlay] = useState({});
  const [latest, setLatest] = useState({});
  const later = dayjs().add(7, "d").format("YYYY-MM-DD");
  const today = dayjs().format("YYYY-MM-DD");
  const ago = dayjs().subtract(7, "d").format("YYYY-MM-DD");

  const number = [1, 2];

  const getUpcoming = async (page) => {
    const json = await (
      await fetch(`
      ${TMDBURL}/upcoming?api_key=${apiKey}&language=ko&page=${page}&region=KR
      `)
    ).json();
    setUpcoming((prevState) => {
      return { ...prevState, json };
    });
  };

  const getNowPlaying = async () => {
    const json = await (
      await fetch(
        `${TMDBURL}/now_playing?api_key=b69ce63df91b692b402e8436d1099819&language=ko&page=1&region=KR`
      )
    ).json();
    setNowPlay(json);
  };
  //980078
  const getMovieId = async () => {
    const json = await (
      await fetch(
        `${TMDBURL}/980078/videos?api_key=b69ce63df91b692b402e8436d1099819&language=en`
      )
    ).json();
    setLatest(json);
  };

  useEffect(() => {
    getUpcoming(2);
    getNowPlaying();
    getMovieId();
  }, []);
  console.log(upcoming);
  console.log(latest);
  return (
    <>
      <Div>
        {upcoming.results?.map((e) => {
          if (e.release_date <= later) {
            return (
              <div>
                <div>{e.title}</div>
                <Img
                  src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}
                ></Img>
                <div>{e.overview}</div>
                <div>{e.id}</div>
              </div>
            );
          }
        })}
      </Div>
      <Div>
        {nowPlay.results?.map((e) => {
          if (e.release_date <= later) {
            return (
              <div>
                <div>{e.title}</div>
                <Img
                  src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}
                ></Img>
                <div>{e.overview}</div>
              </div>
            );
          }
        })}
      </Div>
    </>
  );
};

export default Poster;
