import React, { useEffect, useState } from "react";
import styled from "styled-components";
import ids from "../../channelId/ids";

const Div = styled.div`
  display: flex;
  flex-direction: row;
`;

const Thumbnail = () => {
  const apiKey = "AIzaSyD_0oGw93lCcNaUkmdnEU780lDHOOnb-jI";
  const VIDEOS = "videos";
  const PLAYLISTS = "playlists";
  const YOUTUBEURL = "https://www.googleapis.com/youtube/v3/";
  const [loading, setLoading] = useState(true);
  const [playlist, setPlaylist] = useState([]);

  const getPlaylist = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MavelKorea}&maxResults=50&part=snippet,contentDetails`
      )
    ).json();
    setPlaylist(json);
    setLoading(false);
  };
  console.log(playlist);
  useEffect(() => {
    getPlaylist();
  }, []);

  return (
    <Div>
      <h2>{playlist.items[12].snippet.title}</h2>
      {playlist.items.map((e) => (
        <div key={e.etag}>
          <img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></img>
          <ul>
            <li>{e.snippet.title}</li>
          </ul>
        </div>
      ))}
    </Div>
  );
};
export default Thumbnail;
