import React, { useEffect, useState } from "react";

const App = () => {
  const apiKey = "AIzaSyD_0oGw93lCcNaUkmdnEU780lDHOOnb-jI";
  const channelId = "@ukitivi";
  const [loading, setLoading] = useState(true);
  const [playlist, setPlaylist] = useState([]);

  const getPlaylist = async() => {
    const json = await(
      await fetch(`https://www.googleapis.com/youtube/v3/playlists?key=${apiKey}&id=${channelId}&part=snippet,status`
      )
    ).json();
    setPlaylist(json.data.items);
    setLoading(false)
  }

  useEffect(() => {
    getPlaylist();
  }, []);

  return (
    <div>
      {loading ? 
        <h1>loading...</h1>
       : null}
    </div>
  );
};
export default App;
