import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ids from '../../channelId/ids';
import { apiKey, YOUTUBEURL, PLAYLISTS, PLAYLISTSITEMS } from '../../API/apis';
import Modal from 'react-modal';
import PlayMovie from './PlayMovie';
import { FaStar, FaRegStar } from 'react-icons/fa';
import Poster from './Poster';

const Div = styled.div`
  width: 100%;
  height: 100%;
  padding: 20px;
  display: flex;
  flex-flow: wrap;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
`;

const Img = styled.img`
  width: 240px;
  height: 160px;
  padding-right: 45px;
`;

const Title = styled.p`
  width: 240px;
  color: white;
  word-wrap: normal;
  text-align: center;
  padding-right: 40px;
`;
//모달 스타일
const modalStyle = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backdropFilter: 'blur(5px)',
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    width: '100%',
    zIndex: 10,
    overflow: 'hidden',
    padding: '0px',
    display: 'flex',
    justifyContent: 'center',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'black',
    border: 'none',
    color: 'white',
    borderRadius: '20px',
    overflowy: 'scroll',
    WebkitOverflowScrolling: 'touch',
    zIndex: 10,
    width: '80%',
    marginLeft: '85px',
  },
};

const ModalHeader = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const Btn = styled.div`
  text-align: center;
  font-size: 30px;
  :hover {
    cursor: pointer;
  }
`;
const Plist = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 10px;
`;

const PlistItems = styled.div`
  display: flex;
  flex-direction: row;
  margin: 10px 0px;
  justify-content: left;
  border: 2px solid rgba(255, 255, 255, 0.1);
  border-radius: 10px;
`;
const PlistItemsInfo = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  text-align: center;
  width: 100%;
`;
const PlistItemsTitle = styled.div`
  width: 100%;
  color: white;
  text-align: center;
  padding-right: 40px;
  word-wrap: normal;
`;

const Thumbnail = () => {
  const [marvelPlaylist, setMarvelPlaylist] = useState([]);
  const [MarvelPlist, setMarvelPlist] = useState([]);
  const [cjenmPlaylist, setCjenmPlaylist] = useState([]);
  const [centryPlaylist, setCentryPlaylist] = useState([]);
  const [warnerPlaylist, setWarnerPlaylist] = useState([]);
  const [showboxPlaylist, setShowboxPlaylist] = useState([]);
  const [marvelPlistItem, setMarvelPlistItem] = useState([]);
  const [upcoming, setUpcoming] = useState({});
  const [playlist, setPlaylist] = useState({
    marvelItem: {},
    cjenmItem: {},
    centryItem: {},
    warnerItem: {},
    showBoxItem: {},
  });

  // const getPlaylist = async (channelId) => {
  //   const json = await (
  //     await fetch(`
  //       ${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${channelId}&maxResults=10&part=id,snippet
  //     `)
  //   ).json();
  //   return json.items;
  // };

  const [modal, setModal] = useState(false);

  const getUpcoming = async () => {};
  const getPlaylistMarvel = async () => {
    const json = await (
      await fetch(`${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MarvelKorea}&maxResults=10&part=id,snippet`)
    ).json();
    setMarvelPlaylist(json);
    setPlaylist(() => {
      return {
        ...playlist.cjenmItem,
        ...playlist.centryItem,
        ...playlist.warnerItem,
        ...playlist.showBoxItem,
        marvelItem: json.items,
      };
    });
  };
  const getPlistItems = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTSITEMS}?key=${apiKey}&playlistId=PL9ArOU7415dI9obxf4d-5ezi01fW8XBiv&maxResults=10&part=id,snippet`,
      )
    ).json();
    setMarvelPlistItem(json);
    setPlaylist(() => {
      // return { ...playlist, json };
    });
  };
  const getPlaylistCJENM = async () => {
    const json = await (
      await fetch(`${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.CJENM}&maxResults=10&part=snippet`)
    ).json();
    setCjenmPlaylist(json.items);
    setPlaylist(() => {
      return {
        ...playlist.marvelItem,
        ...playlist.centryItem,
        ...playlist.warnerItem,
        ...playlist.showBoxItem,
        cjenmItem: json.items,
      };
    });
  };
  const getPlaylistCentry = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.centryFox}&maxResults=10&part=snippet,contentDetails`,
      )
    ).json();
    setCentryPlaylist(json.items);
    setPlaylist(() => {
      return {
        ...playlist.marvelItem,
        ...playlist.cjenmItem,
        ...playlist.warnerItem,
        ...playlist.showBoxItem,
        centryItem: json.items,
      };
    });
  };
  const getPlaylistWarner = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.WarnerB}&maxResults=10&part=snippet,contentDetails`,
      )
    ).json();
    setWarnerPlaylist(json.items);
    setPlaylist(() => {
      return {
        ...playlist.marvelItem,
        ...playlist.cjenmItem,
        ...playlist.centryItem,
        ...playlist.showBoxItem,
        warnerItem: json.items,
      };
    });
  };
  const getPlaylistShowbox = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.ShowBox}&maxResults=10&part=snippet,contentDetails`,
      )
    ).json();
    setShowboxPlaylist(json.items);
    setPlaylist(() => {
      return {
        ...playlist.marvelItem,
        ...playlist.cjenmItem,
        ...playlist.centryItem,
        ...playlist.warnerItem,
        showBoxItem: json.items,
      };
    });
  };
  // playlist(ids.MarvelKorea).then(res => {setMarvelPlist(res); console.log(MarvelPlist)});

  useEffect(() => {
    getPlaylistMarvel();
    getPlistItems();
    getPlaylistCJENM();
    getPlaylistCentry();
    getPlaylistWarner();
    getPlaylistShowbox();
    // playlist(ids.MarvelKorea);
  }, []);

  return (
    <Div>
      {/* {marvelPlaylist.items?.map((e) => (
        <div key={e.etag} onClick={() => setModal(true)}>
          <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></Img>
          <Title>{e.snippet.title}</Title>
        </div>
      ))}
      {cjenmPlaylist?.map((e) => (
        <div key={e.etag}>
          <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></Img>
          <Title>{e.snippet.title}</Title>
        </div>
      ))}
      {centryPlaylist?.map((e) => (
        <div key={e.etag}>
          <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></Img>
          <Title>{e.snippet.title}</Title>
        </div>
      ))}
      {warnerPlaylist?.map((e) => (
        <div key={e.etag}>
          <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></Img>
          <Title>{e.snippet.title}</Title>
        </div>
      ))}
      {showboxPlaylist?.map((e) => (
        <div key={e.etag}>
          <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></Img>
          <Title>{e.snippet.title}</Title>
        </div>
      ))} */}
      <Poster />
      {/* 모달영역 */}
      <Modal isOpen={modal} onRequestClose={() => setModal(false)} ariaHideApp={false} style={modalStyle}>
        <ModalHeader>
          <Btn>{window.localStorage.getItem('qT_fjEgPsaA') === null ? <FaRegStar /> : <FaStar />}</Btn>
          <PlayMovie videoId={'qT_fjEgPsaA'} />
        </ModalHeader>
        <Plist>
          {marvelPlistItem.items?.map((e) => (
            <PlistItems key={e.etag}>
              <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail"></Img>
              <PlistItemsInfo>
                <PlistItemsTitle>{e.snippet.title}</PlistItemsTitle>
                <div>{e.snippet.channelTitle}</div>
              </PlistItemsInfo>
            </PlistItems>
          ))}
        </Plist>
      </Modal>
    </Div>
  );
};
export default React.memo(Thumbnail);
