import React, { useEffect, useState } from "react";
import styled from "styled-components";
import ids from "../../channelId/ids";
import { apiKey, YOUTUBEURL, PLAYLISTS } from "../../API/apis";

const Div = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  overflow-y: auto;
`
const imgDiv = styled.div`
  width: 100%;
`

const Img = styled.img`
  display: block;
  width: 280px;
  height: 160px;
  margin: 20px 20px 20px 20px;

`

const Thumbnail = () => {
  const apiKey = "AIzaSyD_0oGw93lCcNaUkmdnEU780lDHOOnb-jI";
  const VIDEOS = "videos";
  const PLAYLISTS = "playlists";
  const YOUTUBEURL = "https://www.googleapis.com/youtube/v3/";
  const [loading, setLoading] = useState(true);
  const [playlist, setPlaylist] = useState([]);

  const getPlaylist = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MavelKorea}&maxResults=50&part=snippet,contentDetails`
      )
    ).json();
    setPlaylist(json);
    setLoading(false);
  };

  useEffect(() => {
    getPlaylist();
  }, []);

  return (
    <Div>
        {playlist.items?.map((e) => (
          <div key={e.etag}>
            <Img src={e.snippet.thumbnails.medium.url} alt="thumbnail. &#13;"></Img>
            <ul>
              <li>{e.snippet.title}</li>
            </ul>
          </div>
        ))}
    </Div>
  );
};
export default Thumbnail;
