import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import ids from "../../channelId/ids";
import { apiKey, YOUTUBEURL, PLAYLISTS } from "../../API/apis";
import ClickMovie from "../part/ClickMovie";

const Div = styled.div`
  width: 100%;
  height: 100%;
  padding: 20px;
  display: flex;
  flex-flow: wrap;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
`;

const Img = styled.img`
  width: 240px;
  height: 160px;
  padding-right: 45px;
`;

const Title = styled.p`
  color: white;
`;
const Thumbnail = () => {
  const [playlist, setPlaylist] = useState([]);
  // const [playlist1, setPlaylist1] = useState([]);
  // const [playlist2, setPlaylist2] = useState([]);
  // const [playlist3, setPlaylist3] = useState([]);
  // const [playlist4, setPlaylist4] = useState([]);
  const [modal, setModal] = useState(false);
  const outside = useRef();

  const getPlaylist = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MarvelKorea}&maxResults=10&part=snippet,contentDetails`
      )
    ).json();
    setPlaylist(json);
  };
  // const getPlaylist1 = async () => {
  //   const json = await (
  //     await fetch(
  //       `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.CJENM}&maxResults=10&part=snippet,contentDetails`
  //     )
  //   ).json();
  //   setPlaylist1(json);
  // };

  useEffect(() => {
    getPlaylist();
  }, []);
  console.log(playlist);
  // console.log(playlist1);
  return (
    <Div>
      {playlist.items?.map((e) => (
        <div key={e.etag} onClick={() => setModal(true)}>
          {modal && (
            <div
              ref={outside}
              onClick={(e) => {
                if (e.target === outside.current) setModal(false);
              }}
            >

              <ClickMovie />
            </div>
          )}

        </div>
      ))}
    </Div>
  );
};
export default Thumbnail;
