    });
  }, []);

  const getOtts = useCallback(() => {
    //netflix
    otts(8).then((res) => {
      setNetflix(res);
    });
    //diseny
    otts(337).then((res) => {
      setDiseny(res);
    });
    //wavve
    otts(356).then((res) => {
      setWavve(res);
    });
  }, []);

  const close = () => {
    setModal(false);
  };

