import React from "react";
import PlayMovie from "../../movie/PlayMovie";
import styled from "styled-components";
import Player from "../../movie/Player";
import YouTube from "react-youtube";
import ids from "../../../channelId/ids";
//
import { useState, useEffect } from "react";
import {
  YOUTUBEURL,
  PLAYLISTS,
  apiKey,
  PLAYLISTSITEMS,
} from "../../../API/apis";
// UCSB5FOwUVnAhGo_o99IhxYA

const HeaderMovie = () => {
  // const channelId = "UCSB5FOwUVnAhGo_o99IhxYA";
  // const Playlists = async () => {
  //   const [playlists, setPlaylists] = useState([]);
  //   const json = await (
  //     await fetch(
  //       `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=UCSB5FOwUVnAhGo_o99IhxYA&maxResults=50&part=snippet`
  //     )
  //   ).json();
  //   setPlaylists(json);
  //   console.log(playlists);
  // };
  //
  const [plistItems, setPlistItems] = useState({});
  const [plist, setPlist] = useState({});
  const [videoId, setVideoId] = useState("");
  const [plistId, setPlistId] = useState("");

  const Playlist = async () => {
    try {
      const json = await (
        await fetch (
          `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MarvelKorea}&part=snippet`
        )
      ).json();
      setPlist(json);
      setPlistId(json.items[0].id)
      console.log(plistId);
    } catch(err) {
      console.log(err);
    }
  }
  const PlaylistItems = async () => {
    try {
      const json = await (
        await fetch(
          `${YOUTUBEURL}${PLAYLISTSITEMS}?key=${apiKey}&playlistId=&part=snippet`
        )
      ).json();
      setPlistItems(json);
      setVideoId(json.items[0].snippet.resourceId.videoId);
    } catch(err) {
      console.log(err);
    }
  };

  useEffect(() => {
    PlaylistItems();
    Playlist();
  }, []);

  return (
    <div>
      <YouTube
        videoId={videoId}
        opts={{
          width: "100%",
          height: "460px",
          playerVars: {
            autoplay: 0, //자동재생 O
            rel: 0,
            modestbranding: 1, // 컨트롤 바에 youtube 로고를 표시하지 않음
          },
        }}
        //이벤트 리스너
        onEnd={(e) => {
          e.target.stopVideo(0);
        }}
      />
      {/* <Player width="960px" height="400px"/> */}
    </div>
  );
};

export default HeaderMovie;
