import React, { useCallback, useEffect, useState } from 'react';
import dayjs from 'dayjs';
import styled from 'styled-components';
import { upComings } from '../../API/upComings';
import { nowPlayings } from '../../API/nowPlayings';

const Wrapper = styled.div`
  width: 100%;
  height: auto;
  display: flex;
  flex-flow: row wrap;
`;

const Img = styled.img`
  width: 200px;
  height: 260px;
`;

const Title = styled.p`
  width: 240px;
  color: white;
  word-wrap: normal;
  text-align: center;
  padding-right: 50px;
  margin-top: 15px;
`;

const Div = styled.div`
  width: 220px;
  height: 300px;
  margin: 8px;
  display: flex;
  flex-direction: column;
  color: white;
  text-align: center;
`;

const Poster = () => {
  const apiKey = 'b69ce63df91b692b402e8436d1099819';
  const TMDBURL = 'https://api.themoviedb.org/3/movie';
  const [upcoming, setUpcoming] = useState({});
  const [nowPlay, setNowPlay] = useState({});
  const [latest, setLatest] = useState({});
  const later = dayjs().add(7, 'd').format('YYYY-MM-DD');
  const today = dayjs().format('YYYY-MM-DD');
  const ago = dayjs().subtract(7, 'd').format('YYYY-MM-DD');

  const getUpcomings = useCallback(() => {
    upComings().then((res) => {
      setUpcoming(res);
    });
  });
  const getNowPlayings = useCallback(() => {
    nowPlayings().then((res) => {
      setNowPlay(res);
    });
  });

  //980078
  const getMovieId = async () => {
    const json = await (
      await fetch(`${TMDBURL}/980078/videos?api_key=b69ce63df91b692b402e8436d1099819&language=en`)
    ).json();
    setLatest(json);
  };

  useEffect(() => {
    upComings();
    nowPlayings();
    getMovieId();
    console.log('first rendering');
  }, []);
  console.log(upcoming);
  console.log(latest);
  return (
    <Wrapper>
      {upcoming.results?.map((e) => {
        if (e.release_date <= later) {
          return (
            <Div>
              <Img src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}></Img>
              <Title>{e.title}</Title>
            </Div>
          );
        }
      })}
      {nowPlay.results?.map((e) => {
        if (e.release_date <= later) {
          return (
            <Div>
              <Img src={`https://image.tmdb.org/t/p/w200/${e.poster_path}`}></Img>
              <Title>{e.title}</Title>
            </Div>
          );
        }
      })}
    </Wrapper>
  );
};

export default Poster;
