import React from "react";
import styled from "styled-components";

const Exp = styled.p`
  border-radius: 10px;
  font-size: 5px;
  width: 190px;
`;

const Btn = styled.button`
  width: 100%;
  height: 50px;
  background: black;
  border: none;
  border-radius: 12px;
  padding-top: 10px;
  font-size: 18px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  :hover {
    background-color: white;
    color: black;
    ${Exp} {
      color: red;
      background-color: white;
      text-align: center;
    }
    span {
      width: 70%;
      background-color: white;
    }
  }
  :focus {
    background-color: gray;
    ${Exp} {
      background-color: gray;
      color: red;
    }
  }
  ${Exp} {
    text-align: center;
    color: black;
    :hover {
      color: red;
    }
  }
`;

const Button = ({ name, index }) => {
  const explain = [
    "* 3일 내에 올라온 예고편만 보여줍니다.",
    "* 개봉한지 1주일 안의 영화만 보여줍니다.",
    "* 상영까지 1주일 남은 예고편들을 보여줍니다.",
    "* Netflix, Tving 등의 ott 들을 보여줍니다.",
  ];
  return (
    <Btn>
      <span>{name}</span>
      <Exp>{explain[index]}</Exp>
    </Btn>
  );
};

export default Button;
