import React, { useContext } from 'react';
import styled from 'styled-components';
import Modal from 'react-modal';
import MovieIdsContext from '../../contexts/bookMarkIds';
import { FaStar, FaRegStar } from 'react-icons/fa';

const modalStyle = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backdropFilter: 'blur(5px)',
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    width: '100%',
    zIndex: 10,
    overflow: 'hidden',
    padding: '0px',
    display: 'flex',
    justifyContent: 'center',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'black',
    border: 'none',
    color: 'white',
    borderRadius: '20px',
    overflowy: 'scroll',
    WebkitOverflowScrolling: 'touch',
    zIndex: 10,
    width: '80%',
    marginLeft: '85px',
  },
};

const NoBookMark = styled.div`
  text-align: center;
  color: red;
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const Title = styled.div`
  font-size: 30px;
  font-weight: bold;
  margin-left: 350px;
`;
const Delete = styled.div`
  margin-left: 150px;
  font-size: 18px;
  font-weight: bold;
  display: flex;
`;
const DeleteText = styled.div`
  margin-top: 12px;
  margin-right: 10px;
`;
const DeleteBtn = styled.button`
  color: red;
  font-size: 40px;
  border: none;
  :hover {
    cursor: pointer;
  }
`;
const BodyWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const BodyMain = styled.div`
  display: flex;
`;
const Img = styled.img`
  width: 200px;
  height: 260px;
`;
const Descript = styled.div`
  width: 800px;
`

const BookModal = (props) => {
  const { movieIds, deleteMovieId, setMovieIds } = useContext(MovieIdsContext);

  return (
    <Modal
      isOpen={props.open}
      onRequestClose={() => {
        props.close();
      }}
      ariaHideApp={false}
      style={modalStyle}
    >
      {movieIds.length === 0 && <NoBookMark>즐겨찾기 한 영화가 없습니다.</NoBookMark>}
      {movieIds.length !== 0 && (
        <Wrapper>
          <HeaderWrapper>
            <Title>즐겨찾기 LIST</Title>
            <Delete>
              <DeleteText>전체 삭제</DeleteText>
              <DeleteBtn>{movieIds.length === 0 ? <FaRegStar /> : <FaStar />}</DeleteBtn>
            </Delete>
          </HeaderWrapper>
          <BodyWrapper>
            {movieIds.map((e) => {
              const movieData = JSON.parse(localStorage.getItem(e));
              return (
                <BodyMain>
                  <Img src={`https://image.tmdb.org/t/p/w200/${movieData.image}`}></Img>
                  <div>
                    <div>제목 : {movieData.title}</div>
                    <div>개봉일 : {movieData.realese}</div>
                    <div>
                      줄거리<br />
                      {movieData.overview}
                    </div>
                  </div>
                </BodyMain>
              );
            })}
            <div>DeleteButton</div>
          </BodyWrapper>
        </Wrapper>
      )}
    </Modal>
  );
};

export default BookModal;
