import React from "react";
import styled from "styled-components";
import { useState } from "react";
import { apiKey, PLAYLISTS, VIDEOS, YOUTUBEURL } from "../../API/apis";
import YouTube from "react-youtube";
import ids from "../../channelId/ids";
import { useEffect } from "react";

const Div = styled.div`
  width: 100%;
  height: 500px;
  background: white;
  border: 1px solid red;
`;

const Header = () => {
  const [loading, setLoading] = useState(true);
  const [playlists, setPlaylists] = useState([]);
  const [videos, setViedos] = useState([]);

  const getVideos = async () => {
    const plist = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MavelKorea}&maxResult=50&part=snippet,contentDetails`
      )
    )
      .json()
      .catch((e) => console.log(e));
    setPlaylists(plist);
    setLoading(false);
    console.log(plist.items[0].id);
  };

  useEffect(() => {
    getVideos();
  }, []);

  return (
    <Div>
      <YouTube
        videoId={playlists.items?.id}
        opts={{
          width: "100%",
          height: "500px",
          playerVars: {
            autoplay: 1, //자동재생 O
            rel: 0, //관련 동영상 표시하지 않음 (근데 별로 쓸모 없는듯..)
            modestbranding: 1, // 컨트롤 바에 youtube 로고를 표시하지 않음
          },
        }}
        //이벤트 리스너
        onEnd={(e) => {
          e.target.stopVideo(0);
        }}
      />
    </Div>
  );
};

export default Header;
