import React, { useContext } from 'react';
import styled from 'styled-components';
import Modal from 'react-modal';
import MovieIdsContext from '../../contexts/bookMarkIds';
import { FaStar, FaRegStar } from 'react-icons/fa';

const modalStyle = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backdropFilter: 'blur(5px)',
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    width: '100%',
    zIndex: 10,
    overflow: 'hidden',
    padding: '0px',
    display: 'flex',
    justifyContent: 'center',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'black',
    border: 'none',
    color: 'white',
    borderRadius: '20px',
    overflowy: 'scroll',
    WebkitOverflowScrolling: 'touch',
    zIndex: 10,
    width: '80%',
    marginLeft: '85px',
  },
};

const NoBookMark = styled.div`
  text-align: center;
  color: red;
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const Title = styled.div`
  font-size: 30px;
  font-weight: bold;
  margin-left: 380px;
`;

const Delete = styled.div`
  margin-left: 150px;
  font-size: 18px;
  font-weight: bold;
  display: flex;
`;

const DeleteText = styled.div`
  margin-top: 12px;
  margin-right: 10px;
`;

const DeleteBtn = styled.button`
  color: red;
  font-size: 40px;
  border: none;
  :hover {
    cursor: pointer;
  }
`;

const BodyWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const BodyMain = styled.div`
  display: flex;
  border: 1px dotted white;
  justify-content: center;
  align-items: center;
  margin: 20px;
`;

const Img = styled.img`
  width: 150px;
  height: 195px;
  margin: 10px;
`;

const Descript = styled.div`
  width: 500px;
  height: 200px;
  margin-left: 30px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-overflow: ellipsis;
`;

const OverView = styled.div`
  height: 100px;
  text-overflow: ellipsis;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BodyDeleteBtn = styled.button`
  color: white;
  margin-left: 20px;
  font-size: 40px;
  border: none;
  :hover {
    cursor: pointer;
  }
`;

const BookModal = (props) => {
  const { movieIds, deleteMovieId, setMovieIds } = useContext(MovieIdsContext);

  const deleteBookMark = (movieId) => {
    deleteMovieId(movieId);
  };
  return (
    <Modal
      isOpen={props.open}
      onRequestClose={() => {
        props.close();
      }}
      ariaHideApp={false}
      style={modalStyle}
    >
      {movieIds.length === 0 && <NoBookMark>즐겨찾기 한 영화가 없습니다.</NoBookMark>}
      {movieIds.length !== 0 && (
        <Wrapper>
          <HeaderWrapper>
            <Title>즐겨찾기 LIST</Title>
            <Delete>
              <DeleteText>전체 삭제</DeleteText>
              <DeleteBtn onClick={() => deleteAllBookMark()}>
                {movieIds.length === 0 ? <FaRegStar /> : <FaStar />}
              </DeleteBtn>
            </Delete>
          </HeaderWrapper>
          <BodyWrapper>
            {movieIds.map((e) => {
              const movieData = JSON.parse(localStorage.getItem(e));
              return (
                <BodyMain>
                  <Img src={`https://image.tmdb.org/t/p/w200/${movieData.image}`}></Img>
                  <Descript>
                    <div>제목 : {movieData.title}</div>
                    <br />
                    <div>개봉일 : {movieData.realese}</div>
                    <br />
                    <OverView>{movieData.overview}</OverView>
                  </Descript>
                  <BodyDeleteBtn onClick={() => deleteBookMark(movieData.videoId)}>
                    {movieIds.includes(movieData.videoId) ? <FaStar /> : <FaRegStar />}
                  </BodyDeleteBtn>
                </BodyMain>
              );
            })}
          </BodyWrapper>
        </Wrapper>
      )}
    </Modal>
  );
};

export default BookModal;
