import React from "react";
import PlayMovie from "../../movie/PlayMovie";
import styled from "styled-components";
import Player from "../../movie/Player";
import YouTube from "react-youtube";
//
import { useState, useEffect } from "react";
import {
  YOUTUBEURL,
  PLAYLISTS,
  apiKey,
  PLAYLISTSITEMS,
} from "../../../API/apis";
// UCSB5FOwUVnAhGo_o99IhxYA

const HeaderMovie = () => {
  // const channelId = "UCSB5FOwUVnAhGo_o99IhxYA";
  // const Playlists = async () => {
  //   const [playlists, setPlaylists] = useState([]);
  //   const json = await (
  //     await fetch(
  //       `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=UCSB5FOwUVnAhGo_o99IhxYA&maxResults=50&part=snippet`
  //     )
  //   ).json();
  //   setPlaylists(json);
  //   console.log(playlists);
  // };
  //
  const [plistItems, setPlistItems] = useState("");
  const PlaylistItems = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTSITEMS}?key=${apiKey}&playlistId=PL9ArOU7415dI9obxf4d-5ezi01fW8XBiv&part=snippet`
      )
    ).json();
    setPlistItems(json);
  };
  useEffect(() => {
    // Playlists(channelId);
    PlaylistItems();
  }, []);
  const title = plistItems.items[0].snippet.resourceId.videoId;
  console.log(title);

  return (
    <div>
      <YouTube
        videoId={title}
        opts={{
          width: "100%",
          height: "460px",
          playerVars: {
            autoplay: 0, //자동재생 O
            rel: 0,
            modestbranding: 1, // 컨트롤 바에 youtube 로고를 표시하지 않음
          },
        }}
        //이벤트 리스너
        onEnd={(e) => {
          e.target.stopVideo(0);
        }}
      />
      {/* <Player width="960px" height="400px"/> */}
    </div>
  );
};

export default HeaderMovie;
