import React, { useEffect, useState } from "react";
import ids from "./channelId/ids";

const App = () => {
  const apiKey = "AIzaSyD_0oGw93lCcNaUkmdnEU780lDHOOnb-jI";
  const channelId = "UCRV0Gb4i99Aj9UiqCE8xlvw";
  const VIDEOS = "videos";
  const PLAYLISTS = "playlists";
  const YOUTUBEURL = "https://www.googleapis.com/youtube/v3/";
  const [loading, setLoading] = useState(true);
  const [playlist, setPlaylist] = useState([]);

  const getPlaylist = async () => {
    const json = await (
      await fetch(
        `${YOUTUBEURL}${PLAYLISTS}?key=${apiKey}&channelId=${ids.MavelKorea}&maxResults=50&part=snippet,contentDetails`
      )
    ).json();
    setPlaylist(json);
    setLoading(false);
  };
  console.log(playlist.items);
  useEffect(() => {
    getPlaylist();
  }, []);

  return (
    <div>
      {loading ? (
        <h1>loading...</h1>
      ) : (
        <div>
          <h2>Thumbnail</h2>
          <h2>{playlist.items[13].snippet.title}</h2>
          </div>)}
    </div>
  );
};
export default App;
